"use client";
import Sidebar from "@/components/Sidebar";

export default function DashboardLayout({
  children,
}: {
  children: React.ReactNode;
}) {
  return (
    <div className="flex h-screen bg-gray-50" dir="rtl">
      {/* ۱. سایدبار با عرض ثابت */}
      <Sidebar />

      {/* ۲. محتوای اصلی که باید به اندازه عرض سایدبار حاشیه داشته باشه */}
      <main className="flex-1 pr-64 overflow-y-auto p-8">
        {children}
      </main>
    </div>
  );
}
